%%%%%%%%%%%%%%%%%%%%%%%% TABLE SCHEMAS DEFINITIONS %%%%%%%%%%%%%%%%%%%%%%%%

xschm 'DayTime': [hour] ==> [daytime].
xschm 'Today': [day] ==> [today].
xschm 'Actions': [location,daytime,today] ==> [action].
xschm 'Threats': [action,activity] ==> ['{threat}'].

%%%%%%%%%%%%%%%%%%%%%%%%%%%% RULES DEFINITIONS %%%%%%%%%%%%%%%%%%%%%%%%%%%%

xrule 'DayTime'/1:
      [hour in [14.000 to 18.000]]
    ==>
      [daytime set afternoon].
xrule 'DayTime'/2:
      [hour in [6.000 to 11.000]]
    ==>
      [daytime set morning]
    :'Actions'.
xrule 'DayTime'/3:
      [hour in [19.000 to 23.000,0.000 to 5.000]]
    ==>
      [daytime set night].
xrule 'DayTime'/4:
      [hour in [12.000 to 13.000]]
    ==>
      [daytime set noon].

xrule 'Today'/1:
      [day in [mon to fri]]
    ==>
      [today set workday]
    :'Actions'.
xrule 'Today'/2:
      [day in [sat to sun]]
    ==>
      [today set weekend].

xrule 'Actions'/1:
      [location eq home,
       daytime eq morning,
       today eq workday]
    ==>
      [action set leaving]
    :'Threats'.
xrule 'Actions'/2:
      [location eq home,
       daytime eq night,
       today eq any]
    ==>
      [action set sleeping].
xrule 'Actions'/3:
      [location eq work,
       daytime eq afternoon,
       today eq workday]
    ==>
      [action set leaving].
xrule 'Actions'/4:
      [location eq outside,
       daytime eq any,
       today eq any]
    ==>
      [action set travelling].

xrule 'Threats'/1:
      [action in [leaving,travelling],
       activity in [walking,running]]
    ==>
      ['{threat}' set [pedestrian_threat]].
xrule 'Threats'/2:
      [action in [leaving,travelling],
       activity in [driving,cycling]]
    ==>
      ['{threat}' set [road_threat]].
xrule 'Threats'/3:
      [action in [sleeping,working],
       activity in [idle]]
    ==>
      ['{threat}' set [building_threat]].

